(()=>{let e=document.querySelector("fb-uploader");e.innerHTML='\n\t\t\t<div class="uploader" id="uploader">\n\t\t\t\t<p>Drop your files here</p>\n\t\t\t</div>\n\t\t\t<form id="uploadForm" style="display: none;">\n\t\t\t\t<input type="file" id="files" name="files" multiple>\n\t\t\t\t<button id="btnSubmit"></button>\n\t\t\t</form>\n\t\t';let t=e.querySelector("#uploader"),n=e.querySelector("#uploadForm"),r=n.querySelector("#files"),o=n.querySelector("#btnSubmit"),l=e.getAttribute("data-upload-url");t.addEventListener("dragover",function(e){e.preventDefault(),t.classList.add("hover")}),t.addEventListener("dragleave",function(e){e.preventDefault(),t.classList.remove("hover")}),t.addEventListener("drop",function(e){e.preventDefault(),t.classList.remove("hover"),r.files=e.dataTransfer.files,o.click()}),n.addEventListener("submit",function(e){e.preventDefault();let t=new XMLHttpRequest;t.open("POST",l),t.send(new FormData(n)),t.onload=(()=>{console.log("Upload done")})})})();